mapper.select:
select signature_id, a.company_id, signature_name, fdd_signature_id, fdd_signature_type, fdd_signature_scope, fdd_signature_sub_info, a.create_time, create_by_user_id, b.modify_time, modify_by_user_id, fdd_signature_img_base64, b.company_name, b.customer_id from sign_company_signature a, company b where a.company_id = b.company_id
AND a.company_id = #{companyId}
AND fdd_signature_scope = #{fddSignatureScope}

mapper.insert:
insert into sign_company_signature (signature_id, company_id, signature_name, 
  fdd_signature_id, fdd_signature_type, fdd_signature_scope, 
  fdd_signature_sub_info, create_time, create_by_user_id, 
  modify_time, modify_by_user_id, fdd_signature_img_base64
  )
values (#{signatureId,jdbcType=BIGINT}, #{companyId,jdbcType=BIGINT}, #{signatureName,jdbcType=VARCHAR},
  #{fddSignatureId,jdbcType=VARCHAR}, #{fddSignatureType,jdbcType=INTEGER}, #{fddSignatureScope,jdbcType=INTEGER},
  #{fddSignatureSubInfo,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{createByUserId,jdbcType=BIGINT}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{modifyByUserId,jdbcType=BIGINT}, #{fddSignatureImgBase64,jdbcType=LONGVARBINARY}
  )
